var request = require('request'),
    cheerio = require('cheerio');

exports.getTitle = function (uri, cb) {
    'use strict';

    request({
        uri: uri
    }, function (error, response, body) {
        var $,
            title;

        if (error) {
            return cb(error);
        }

        $ = cheerio.load(body);

        title = $('title').text().trim();

        cb(null, title);
    });
};