var website = require('../'),
    assert  = require('assert');

module.exports = function () {
    'use strict';

    website.getTitle('http://google.de', function (err, title) {
        assert.equal(err, null, 'There is an error object while fetching the title.');
        assert.ok(title, 'Title is not okay.');
        assert.equal(title, 'Google');
    });
};